/**
 * Seed CVQ items into database
 * Should be called during initial database seeding
 */
export async function seedCVQItems() {
  console.log('ðŸ“‹ Seeding CVQ items...');
  
  try {
    // Check if CVQ items already exist
    const existingItems = await db.select().from(cvqItems);
    
    if (existingItems.length > 0) {
      console.log(`  CVQ items already exist (${existingItems.length} items), skipping...`);
      return;
    }

    // Insert all 21 CVQ items (id and createdAt auto-generated)
    await db.insert(cvqItems).values(CVQ_SEED_DATA as any);
    
    console.log(`âœ“ Created ${CVQ_SEED_DATA.length} CVQ items (7 domains Ã— 3 questions)`);
    console.log('  Domains: Achievement, Benevolence, Universalism, Self-Direction, Security, Power, Hedonism');
  } catch (error) {
    console.error('Error seeding CVQ items:', error);
    throw error;
  }
}
